import React, { Component } from 'react';
import {Button, Dialog, TextInputField} from 'evergreen-ui';
import {AdvancedOptionsPockets} from './AdvancedOptionsPockets.js';
import * as utils from '../utils.js';
import store from '../store.js';

class PocketButton extends Component {

  state = {
    isShown: false,
    showAdvance: false,
    cutDepthValid: true,
    toolDiameterValid: true,
    stepoverPercentageValid: true,
    toleranceValid: true,
    jogHeightValid: true,
    jogRateValid: true,
    feedRateValid: true,
    dogboneOptions: [
      { label: 'true', value: true },
      { label: 'false', value: false },
    ],
    dogboneTemp: params.dogbone,
    parameters: params,
  }

  render() {

    return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title="Pocket Cut Basic Settings"
          onCloseComplete={() => this.setState({isShown:false})}
          onConfirm={() => {
            let valid = this.state.cutDepthValid && this.state.toolDiameterValid &&
                        this.state.stepoverPercentageValid && this.state.toleranceValid &&
                        this.state.jogHeightValid && this.state.jogRateValid && this.state.feedRateValid;
            if (valid) {
              let cutDepth = parseFloat(document.getElementsByName("cutDepth")[0].value);
              let toolDiameter = parseFloat(document.getElementsByName("toolDiameter")[0].value);
              let stepoverPercentage = parseFloat(document.getElementsByName("stepoverPercentage")[0].value); //must be between 1 and 100%
              let tolerance = parseFloat(document.getElementsByName("tolerance")[0].value);
              let jogHeight = parseFloat(document.getElementsByName("jogHeight")[0].value);
              let jogRate = parseFloat(document.getElementsByName("jogRate")[0].value);
              let feedRate = parseFloat(document.getElementsByName("feedRate")[0].value);

              let dogbone = this.state.dogboneTemp;

              //derived parameters
              let passDepth = cutDepth/2;
              let toolRadius = toolDiameter/2;
              let compensatedRadius = toolRadius + tolerance;
              let tempFloor = Math.ceil(cutDepth/passDepth); //should this be ceiling or floor, I think ceil
              let actualPassDepth = cutDepth/tempFloor;
              let depthOfPasses = utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);

              //settings will be compiled to a single object here
              let parameters = utils.deepcopy(this.state.parameters);

              parameters["dogbone"] = dogbone;

              if (!isNaN(tolerance)) { parameters["tolerance"] = tolerance; }

              if (!isNaN(toolDiameter)) {
                parameters["toolDiameter"] = toolDiameter;
                parameters["compensatedRadius"] = compensatedRadius;

              }
              if (!isNaN(cutDepth)) {
                parameters["cutDepth"] = cutDepth;
                parameters["depthOfPasses"] = depthOfPasses;
              }
              if (!isNaN(feedRate)) { parameters["feedRate"] = feedRate; }
              if (!isNaN(jogHeight)) { parameters["jogHeight"] = jogHeight; }
              if (!isNaN(jogRate)) { parameters["jogRate"] = jogRate; }

              if (!isNaN(stepoverPercentage)) {
                stepoverPercentage = stepoverPercentage/100;
                parameters["stepoverPercentage"] = stepoverPercentage;
              }

              console.log("parameters", parameters);

              this.setState({
                isShown:false,
                parameters
              });

              //update app state here

            }
          }}>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({cutDepthValid:valid})
            }}
            isInvalid={!this.state.cutDepthValid}
            name="cutDepth"
            description={(this.state.cutDepthValid) ? "" : "please enter a valid number"}
            label="Depth of Cut for this Pocket"
            placeholder={String(this.state.parameters.cutDepth)}/>

          {this.state.showAdvanced &&
          <div>
          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({toolDiameterValid:valid})
            }}
            isInvalid={!this.state.toolDiameterValid}
            name="toolDiameter"
            description={(this.state.toolDiameterValid) ? "" : "please enter a valid number"}
            label="Tool Diameter"
            placeholder={String(this.state.parameters.toolDiameter)}/>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              valid = 0 < parseFloat(e.target.value) && parseFloat(e.target.value) <= 100;
              //process value here
              this.setState({stepoverPercentageValid:valid})
            }}
            isInvalid={!this.state.stepoverPercentageValid}
            name="stepoverPercentage"
            description={(this.state.stepoverPercentageValid) ? "" : "please enter a valid number"}
            label="Stepover Percentage (this is only relevant for pocket cuts)"
            placeholder={String(this.state.parameters.stepoverPercentage * 100)}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({feedRateValid:valid})
              }}
              isInvalid={!this.state.feedRateValid}
              name="feedRate"
              description={(this.state.feedRateValid) ? "" : "please enter a valid number"}
              label="Feed Rate"
              placeholder={String(this.state.parameters.feedRate)}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({toleranceValid:valid})
                }}
                isInvalid={!this.state.toleranceValid}
                name="tolerance"
                description={(this.state.toleranceValid) ? "" : "please enter a valid number"}
                label="Tolerance"
                placeholder={String(this.state.parameters.tolerance)}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({jogRateValid:valid})
              }}
              isInvalid={!this.state.jogRateValid}
              name="jogRate"
              description={(this.state.jogRateValid) ? "" : "please enter a valid number"}
              label="Jog Rate"
              placeholder={String(this.state.parameters.jogRate)}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({jogHeightValid:valid})
                }}
                isInvalid={!this.state.jogHeightValid}
                name="jogHeight"
                description={(this.state.jogHeightValid) ? "" : "please enter a valid number"}
                label="Jog Height"
                placeholder={String(this.state.parameters.jogHeight)}/>

              <Heading marginTop={20} size={400} color={"rgba(67, 90, 111, .91)"}>Auto Dogbone</Heading>
              <SegmentedControl
                marginTop={4}
                options={this.state.dogboneOptions}
                value={this.state.dogboneTemp}
                onChange={value => {
                  let dogboneTemp = value;
                  this.setState({ dogboneTemp });
                }}
              />
              </div>}
        </Dialog>
        <Button onClick={() => this.setState({isShown:true})}>Pocket</Button>
      </div>
    );
  }
}

export {PocketButton};
