import * as mjs from 'makerjs';
import * as s2p from 'shape2path';
import * as svgson from 'svgson';
import * as utils from './utils.js';
import {flattenSVG} from 'flatten-svg';

import store from "./store.js"; //this is my global state library, this is the only file that will getState() it will pass that to all sub components or should i import store in respective components

const uploadSVGFlatten = (e) => {

  //main event
  let files = e.target.files;
  let file = files[0];

  var reader = new FileReader();

  reader.onload = (event) => {
    let text = event.target.result;

    console.log(text);
  };

  reader.readAsText(file);
}

const uploadSVG = (e) => {
  uploadSVGFlatten(e);
  //helper function
  const replaceWithPath = (child) => { //TODO: why cant I put this in a different file
    console.log("child", child);
    console.log("child name", child.name);

    if (child.name === "g" || child.name === "defs") {
      return child.children.map(grandchild => replaceWithPath(grandchild));
    }

    if (child.name === "rectangle" || child.name === "rect") {
      let x = child.attributes.x;
      let y = child.attributes.y;
      let width = child.attributes.width;
      let height = child.attributes.heigth;
      let rx = child.attributes.rx;
      let ry = child.attributes.ry;


      if (child.name === "rect") height = child.attributes.height; //TODO: HACK: no idea why I need to do this

      let temp = s2p.rect({x, y, width, height, rx, ry});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "circle") {
      let cx = child.attributes.cx;
      let cy = child.attributes.cy;
      let r = child.attributes.r;

      let temp = s2p.rect({cx, cy, r});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "ellipse") {
      let cx = child.attributes.cx;
      let cy = child.attributes.cy;
      let rx = child.attributes.rx;
      let ry = child.attributes.ry;

      let temp = s2p.ellipse({cx, cy, rx, ry});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "polygon") {
      let points = child.attributes.points;

      let temp = s2p.polygon({points});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "polyline") {
      let points = child.attributes.points;

      let temp = s2p.polyline({points});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "line") {
      let x1 = child.attributes.x1;
      let x2 = child.attributes.x2;
      let y1 = child.attributes.y1;
      let y2 = child.attributes.y2;

      let temp = s2p.line({x1, x2, y1, y2});
      return mjs.importer.fromSVGPathData(temp);
    }

    if (child.name === "path") {
      let makerjsPath = mjs.importer.fromSVGPathData(child.attributes.d);
      console.log("mjspath", makerjsPath);
      return makerjsPath;
    }
  }

  //main event
  let files = e.target.files;
  let file = files[0];

  var reader = new FileReader();

  reader.onload = (event) => {
    let text = event.target.result;

    svgson.parse(text).then((json) => {
      let svg = json.children.map(child => {
        return replaceWithPath(child);
      });

      console.log(svg);

      svg = svg.flat(1); //TODO: what does the argument do again?

      //TODO: update state to have makerjs models
      //console.log("svg", svg);
      let models = {};

      svg.forEach(shape => {
        if (Array.isArray(shape)) {
          shape.filter(entry => typeof entry === "object")
               .forEach(entry => {
                 shape = entry[0];
               })
        }; //TODO: HACK

        console.log("shape", shape);

        let chains = mjs.model.findChains(shape);

        if (chains === undefined) return //TODO: HACK

        chains.forEach(chain => {
          models[utils.makeID()] = mjs.chain.toNewModel(chain);
        })

      //  models[utils.makeID()] = shape;

      })

      // console.log("models", models);

      let newShapes = {...store.state.shapes, ...models}

      store.update({shapes: newShapes});

      //ultimately I will set state of app
    });
  };

  reader.readAsText(file);
}

export {uploadSVG};
