import React, { Component, useState } from 'react';
import {Button, Dialog, TextInputField} from 'evergreen-ui';
import * as utils from '../utils.js';
import store from '../store.js';

const MaterialThickness = ({firstSubmit}) => {
  state = {
    isShown: false,
    value: "...",
    valid: true,
  }

  return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title="Material Thickness"
          onCloseComplete={() => this.setState({isShown:false})}
          onConfirm={() => {
            if (this.state.valid) {
              let value = document.getElementsByName("thickness")[0].value;
              if (this.state.value === "...") {
                firstSubmit("thicknessSubmit");
              }
              this.setState({
                isShown:false,
                value
              });

              //update app state here
              store.update({thickness:value});
            }

          }}>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({valid})
            }}
            isInvalid={!this.state.valid}
            name="thickness"
            placeholder={this.state.value}
            description={(this.state.valid) ? "" : "please enter a valid number"}
            label=""
          />
        </Dialog>
        <Button onClick={() => this.setState({isShown:true})}>{`Thickness: ${this.state.value}`}</Button>
      </div>
    );
}

export {MaterialThickness};
