import React, { Component } from 'react';
import {Table, Checkbox, Button} from 'evergreen-ui';
import {AdvancedOptions} from './AdvancedOptions.js';

import store from '../store.js';

const ToolpathList = ({state}) => {

  return (
    <Table>
      <Table.Head>
        <Table.TextHeaderCell>name</Table.TextHeaderCell>
        <Table.TextHeaderCell>type</Table.TextHeaderCell>
        <Table.TextHeaderCell>modify</Table.TextHeaderCell>
        <Table.TextHeaderCell>reorder</Table.TextHeaderCell>
        <Table.TextHeaderCell>select/view</Table.TextHeaderCell>
      </Table.Head>
      <Table.Body height={240}>
        {state.toolpaths.map((path, i) => (
          <Table.Row key={path.id}>
            <Table.TextCell>{path.name}</Table.TextCell>
            <Table.TextCell>{path.type}</Table.TextCell>
            <Table.TextCell><AdvancedOptions params={path.parameters} default={false} index={i}/></Table.TextCell>
            <Table.TextCell>^ v</Table.TextCell>
            <Table.TextCell>
              <Checkbox
                checked={path.selected}
                onChange={e => {
                  let toolpaths = state.toolpaths;
                  let toolpath = toolpaths[i];

                  toolpath.selected = e.target.checked;

                  store.update({ toolpaths })
                }}
              />
            </Table.TextCell>
          </Table.Row>
        ))}
        <Table.Row>
          <Table.TextCell><Button>Export</Button></Table.TextCell>
          <Table.TextCell><Button>Delete</Button></Table.TextCell>
          <Table.TextCell><Button>(De)Select All</Button></Table.TextCell>
        </Table.Row>
      </Table.Body>
    </Table>
  )
}

export {ToolpathList}
