import React, { Component } from 'react';
import {Button, SegmentedControl} from 'evergreen-ui';

class Units extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: [
        { label: 'mm', value: 'mm' },
        { label: 'in', value: 'in' },
      ],
      value: 'mm',
    }
  }

  render() {
    return (
      <div>
        Units: &nbsp
        <SegmentedControl
          width={240}
          options={state.options}
          value={state.value}
          onChange={value => setState({ value })}
        />
      </div>
    );
  }
}

export {Units};
