import React, { Component } from 'react';
import './App.css';

import * as s2p from 'shape2path';
import * as mjs from 'makerjs';

//import svg and turn into paths
let t = s2p.rect({x: 0, y: 120, width: 200, height: 200, rx: 20, ry: 10});
let m = mjs.importer.fromSVGPathData(t);

//generate gcode data
let cutDepth;
let passDepth;
let toolDiameter;
let numberOfOffsets;
let stepover;
let tolerance;
let climbDirection;
let insideCutting;
let millOuterFirst;

let toolRadius = toolDiameter/2;

let nSeries = toolDiameter * stepover;
let compensatedRadius = toolRadius + cutoutTolerance;

let series = (s, n, c) => {
  let l = [];
  l.push(s);
  for (let i = 0; i < c; i++) {
    l.push(l[i] + n);
  }

  return l;
}

console.log(series(1, 1, 1))
let c1 = mjs.model.expandPaths(m, 8, 1);
console.log(c1);

let c2 = mjs.model.findChains(c1);
console.log(c2)

let svg = mjs.exporter.toSVG(c1);

svg = `<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" ${svg.slice(4)}`;

class App extends Component {
  render() {
    return (
      <div dangerouslySetInnerHTML={{__html: svg}}/>
    );
  }
}

export default App;
