import React, { Component } from 'react';
import {Button, SelectMenu} from 'evergreen-ui';
import store from '../store.js';

class MaterialType extends Component {

  state = {
    selected: null
  };

  render() {
    return (
      <SelectMenu
        title="Select Material"
        options={
          ['Oak', 'Plywood', 'Cherry', 'HDF', 'MDF', 'Aluminum', 'Steel']
            .map(label => ({ label, value: label }))
        }
        selected={this.state.selected}
        onSelect={item => {
          if (this.state.selected === null) {
            this.props.firstSubmit("materialSubmit");
          }
          this.setState({
            selected: item.value,
          })
          //update app state here
          store.update({material:item.value});
        }}
      >
        <Button>Material: {this.state.selected || '...'}</Button>
    </SelectMenu>
    );
  }
}

export {MaterialType};
