import createStore from 'pure-store';
import * as params from './parameters.js';
import * as utils from './utils.js';

import {profile} from './toolpaths/profile.js';
import {pocket} from './toolpaths/pocket.js';

import {kk} from './joints/koshikake.js';

import {line} from './unmakerjs/primitives.js'


let test = kk;
let testProfile = test.models.profile;
let testPocket = test.models.pocket;
let testContour = [line([0,0],[40,40]), line([40,40],[20,40]), line([20,40],[0,0])];
let profileCut = profile(testContour, params);
let pocketCut = pocket(testPocket, params);

let defaultParameters = utils.deepcopy(params);


const store = createStore({
  shapes: {
    1: testProfile,
    2: testPocket
  },
  contours: {
    3: testContour,
  },
  toolpaths:[
    {
      type: "profile",
      name: "Profile 1",
      parameters: defaultParameters,
      sourceGeometryID: "3",
      geometry: profileCut,
      id: utils.makeID(),
      cutSelected: true,
    },
    // {
    //   type: "pocket",
    //   name: "Pocket 1",
    //   parameters: defaultParameters,
    //   sourceGeometryID: "2",
    //   geometry: pocketCut,
    //   id: utils.makeID(),
    //   cutSelected: true,
    // }
  ],
  selected: [],
  hovered: [],
  material: "wood",
  thickness: 34,
  units: "mm",
  zero: "bottomleft",
  defaultParameters: defaultParameters, //need to generate these from material, thickness, units
});

export default store;
