// these are the default settings

import * as utils from './utils.js';

//name
let name = "anon"

//units
let units = "mm"

//generate gcode data
let cutDepth = 8.5;
let toolDiameter = 4;

let stepoverPercentage = 60;

let tolerance = 0;

//let climbDirection = false;
let offset = "outside";
//let millOuterFirst = false;

let jogHeight = 10;
let jogRate = 6000;

let tabs = false;
let tabThickness = 0;
let dogbone = false;

//calculate from material and bit
let feedRate = 2200;
let plungeRate = 1000;

export const depthOfPasses = (cutDepthArg, passDepthArg) => {
  let tempFloor = Math.ceil(cutDepthArg/passDepthArg); //should this be ceiling or floor, I think ceil
  let actualPassDepth = cutDepthArg/tempFloor;

  return utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);
};

export const params = {
  name,
  dogbone,
  tabThickness,
  tabs,
  tolerance,
  toolDiameter,
  toolRadius: toolDiameter/2,
  compensatedRadius: toolDiameter/2 + tolerance,
  cutDepth,
  passDepth,
  jogHeight,
  offset,
  stepoverPercentage,
  feedRate,
  jogRate,
  units,
  plungeRate,
}
