import React, { Component } from 'react';
import {Button, Dialog, TextInput} from 'evergreen-ui';
import * as utils from '../utils.js';

class PocketButton extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isShown: false,
      valid: true,
    }
  }

  render() {

    return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title="Material Thickness"
          onCloseComplete={() => this.setState({isShown:false})}
          onConfirm={() => {
            if (this.state.valid) {
              let value = document.getElementsByName("depth")[0].value;
              this.setState({
                isShown:false,
              });

              //update app state here

            }
          }}>

          <TextInput
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({valid})
            }}
            isInvalid={!this.state.valid}
            name="depth"
            description={(this.state.valid) ? "" : "please enter a valid number"}
            label="Depth of Cut"
          />
        </Dialog>
        <Button onClick={() => this.setState({isShown:true})}>Pocket</Button>
      </div>
    );
  }
}

export {PocketButton};
