import React, { Component } from 'react';
import './App.css';

import * as utils from './utils.js';

import * as s2p from 'shape2path';
import * as mjs from 'makerjs';
import * as svgson from 'svgson';

import {Workplane} from './Workplane.js'

import Button from '@material-ui/core/Button';

import {kk} from './joints/koshikake.js';
import {profile} from './toolpaths/profile.js';
import {pocket} from './toolpaths/pocket.js';

import * as params from './parameters.js';

// utils.download(filename, text);

class App extends Component {
  constructor() {
    super();

    this.state = {
      shapes: [],
    }
  };

  uploadSVG(e) {
    const replaceWithPath = (child) => {
      if (child.name === "g") {
        return child.children.map(grandchild => replaceWithPath(grandchild));
      }

      if (child.name === "rectangle") {
        let temp = s2p.rect({x: 0, y: 120, width: 200, height: 200, rx: 20, ry: 20});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "path") {
        let makerjsPath = mjs.importer.fromSVGPathData(child.attributes.d);
        return makerjsPath;
      }
    }

    let files = e.target.files;
    let file = files[0];

    var reader = new FileReader();

    reader.onload = (event) => {
        let text = event.target.result;

        // let regexPaths = /<path[\s\S]*\/>/g;
        // let foundPaths = file2.match(regexPaths);

        svgson.parse(text).then((json) => {
          let svg = json.children.map(child => {
            return replaceWithPath(child);
          });

          svg = svg.flat(1); //TODO: what does the argument do again?

          //TODO: update state to have makerjs models
          console.log(svg);
        });

    }

    reader.readAsText(file);
  }

  render() {
    //should I put everying in a list or all to one model, there seem to be some issues with origins if I mjs.exporter.toSVG() seperately
    let test = kk;
    //test = mjs.model.originate(test);
    let testProfile = test.models.profile;
    let testPocket = test.models.pocket;
    let profileCut = profile(testProfile, params);
    let pocketCut = pocket(testPocket, params);

    // let drawings = [test, profile(test)];
    // drawings = drawings.map(d => mjs.exporter.toSVG(d));

    //testing if internal outlines will make acceptable pocket paths, appears so
    //let p2 = pocket(testPocket, params).drawing;
    let t1 = new mjs.models.Oval(20,20)
    let t2 = mjs.model.move(new mjs.models.Oval(20,20), [15,0])
    let test0 = mjs.model.combineUnion(t1, t2);
    let test1 = mjs.model.outline(test0, 5, 1, true)
    // console.log(test0)
    // test0 = mjs.model.move(test0, [50, 0]);

    let model = {
      models: {
        profileSource: testProfile,
        profileCut: profileCut.drawing,
        pocketSource: testPocket,
        pocketCut: pocketCut.drawing,
        //pocketCut2: pocket(p2, params).drawing,
        // test0,
        // test1,
      }
    }

    // model = mjs.model.originate(model);
    // model = mjs.model.zero(model);

    model['paths'] = {
      origin: new mjs.paths.Circle(params.compensatedRadius)
    }

    let modelToView = mjs.model.scale(utils.deepcopy(model), 2); //TODO: make drawing easier to see, but this ruins toolpaths

    let viewModel = [mjs.exporter.toSVG(modelToView)];

    let testModel = mjs.exporter.toSVG(modelToView)

    //console.log(testModel);

    const extractClosedPaths = (model) => {
      // let svgSearch = String(svg);
      //
      // console.log(svgSearch);

      // let keysSVGs = Object.entries(modelToView.models).map((k, v) => [k, mjs.exporter.toSVG(v)] );
      //
      // console.log(keysSVGs)

      let svg = mjs.exporter.toSVG(model);

      // let regexSVGBoilerplate = /<svg[^>]*/i;
      // let foundsvgBoilerPlate = svg.match(regexSVGBoilerplate)[0] + '>';

      let boilerplate = (id) => {

        return [ //custom id and styling can go here
          `<g
            id="${id}"
            stroke-linecap="round"
            fill-rule="evenodd"
            font-size="9pt"
            stroke="#000"
            stroke-width="0.25mm"
            fill="none"
            style="stroke:#000;stroke-width:0.25mm;fill:none">
              <path d="`,

              ` vector-effect="non-scaling-stroke"/>
          </g>`
        ];
      };

      let regexPaths = /<path[\s\S]*\/>/g;
      let foundPaths = svg.match(regexPaths);

      let firstPath = foundPaths[0];

      let regexClosed = /m[^z]*/gi;
      let foundClosed = firstPath.match(regexClosed)


      foundClosed = foundClosed.map(s => boilerplate("hi")[0] + s + 'Z' + boilerplate("hi")[1]);
      foundClosed = foundClosed.join('');

      return foundClosed
    };

    //viewModel = extractClosedPaths(modelToView);
    console.log(mjs.exporter.toSVG(modelToView))

    //extractClosedPaths(modelToView);

    viewModel = mjs.exporter.toSVG(modelToView);
    viewModel.push(Object.values(modelToView.models).map(m => mjs.exporter.toSVG(m)));
    console.log(viewModel);;

    // viewModel = [];
    // Object.entries(model.models).map( ([k, v]) => {
    //   console.log(v)
    //   viewModel.push(mjs.exporter.toSVG(v));
    // })

    return (
      <div style={{
                    margin: 0,
                    height: "100%",
                    overflow: "hidden"
                  }}>
        <Workplane groups={viewModel}/>

        <div>
          <input
            id="myInput"
            type="file"
            ref={(ref) => this.upload = ref}
            style={{ display: 'none' }}
            onChange={(e) => this.uploadSVG(e)}/>
          <Button
            variant="contained"
            color="primary"
            onClick={(e) => this.upload.click()}
            >
              Upload SVG
          </Button>
          &nbsp;
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              utils.download(profileCut.filename, profileCut.gcode);
              utils.download(pocketCut.filename, pocketCut.gcode);
            }}
            >
              download gcode
          </Button>
        </div>

      </div>
    );
  }
}

export default App;
