// makerjs model, depth, params -> pocket toolpath
import * as mjs from 'makerjs';
import clipperOffset from './clipperOffset.js';

const STRANGE_CORRECTION = 0.0000001; //TODO: why is this neccessary otherwise outline shits the bed

const len = (toolpath) => Object.keys(toolpath.models).length;

const isToolpath = (toolpath) => {
  //console.log("toolpath", toolpath);

  if (len(toolpath) === 1) {
    return true;
  } else if (len(toolpath) > 1) {
    return true;
  } else {
    return false;
  }
}

const intToolpath = (geo, radius, corners = 1) => {
  // console.log("geo", geo);
  // console.log(len(geo));
  // let corners = (len(geo) === 1) ? 1 : 0;

  let pass;

  try {
    pass = mjs.model.outline(geo, radius + STRANGE_CORRECTION, corners, true);
  } catch {
    corners = 0;
    pass = mjs.model.outline(geo, radius + STRANGE_CORRECTION, corners, true);
  }

  return pass
}

const intToolpaths = (geo, params) => {
  let models = {}
  let count = 0;

  let pass = intToolpath(geo, params.compensatedRadius);

  if (isToolpath(pass) !== true) {
    return {models:{}};
  }

  while ( isToolpath(pass)) {
    models[count] = pass;
    count++;
    pass = intToolpath(models[count - 1], params.compensatedRadius * params.stepoverPercentage/100);
    //pass = clipperOffset(models[count - 1], params.compensatedRadius * params.stepoverPercentage/100)
    if (count < 20) console.log(clipperOffset(models[count - 1], params.compensatedRadius * params.stepoverPercentage/100));

    // this is a strange issue
    // not entirely sure why mjs has issues with some corner parameters when there are sharp separate corners in model
    // try {
    //   pass = intToolpath(models[count - 1], params.compensatedRadius * params.stepoverPercentage, corners);
    // } catch {
    //   corners = 0;
    // }
    //
    // if (len(pass) > 1) {
    //   corners = 0;
    // }

    //console.log("pass", pass, "count", count, "ispath", isToolpath(pass));
  }

  let model = {models}

  //console.log("model", model)

  return model;
}

const pocket = (model, params) => {
  let p = model;

  let outlines = intToolpaths(p, params);
  let iterOut = Object.values(outlines.models);

  let chains = iterOut.map(x => mjs.model.findChains(x));

  var divisions = chains.map(c => c.map(c2 => Math.floor(c2.pathLength / params.minimumSpacing)));

  var spacing = chains.map((c,i) => c.map((c2, j) => c2.pathLength / divisions[i][j]));

  let keyPoints = chains.map((c, i) => c.map((c2, j) => mjs.chain.toKeyPoints(c2, spacing[i][j])));

  // console.log(keyPoints);

  let gcodePoints = keyPoints.map(c => {
    let raise = c.length > 1;

    return c.map(c2 => {
      let firstPoint = c2[0];
      firstPoint = `G1 X${firstPoint[0]} Y${firstPoint[1]} F${params.feedRate}`;

      if (raise) {
        return [
          `G1 Z${params.jogHeight} F${params.jogRate}`,
          firstPoint, //want to jog to this one
          "plunge",
          ...c2.map(c3 => `G1 X${c3[0]} Y${c3[1]} F${params.feedRate}`),
          firstPoint,
          `G1 Z${params.jogHeight} F${params.jogRate}`,
        ]
      } else {
        return [
          firstPoint,
          "plunge",
          ...c2.map(c3 => `G1 X${c3[0]} Y${c3[1]} F${params.feedRate}`),
          firstPoint
        ]
      }
    });
  });

  let paths = gcodePoints.flat(1);

  // let firstPoint = keyPoints[0][0][0];

  let paths2 = params.depthOfPasses.map((p,i) => [
    ...paths.flat(1).map(x => (x === "plunge") ? `G1 Z${params.depthOfPasses[i]} F12.0` : x),
  ])

  // console.log(paths2)

  paths2 = paths2.flat(1);

  let preamble = [params.units, "G90"];

  let gcode = [
    ...preamble,
    "(end of preamble)",
    `G1 Z${params.jogHeight} F${params.jogRate}`,
    // `G0 X${firstPoint[0]} Y${firstPoint[1]}`,
    ...paths2,
    `G1 Z${params.jogHeight} F${params.jogRate}`,
  ]

  // write to a new file named
  let filename = 'testGcodePocket.gcode';

  let text = gcode.join('\n');

  return {drawing: outlines, gcode: text, filename};
};


export {pocket};
