import React, { useState } from 'react';
import {SegmentedControl} from 'evergreen-ui';
import store from '../store.js';

const Units = () => {
  const [state, setState] = useState({
    options: [
      { label: 'mm', value: 'mm' },
      { label: 'in', value: 'in' },
    ],
    value: 'mm',
  });

  return (
      <SegmentedControl
        width={80}
        options={state.options}
        value={state.value}
        onChange={value => {
          // update app state here
          setState({...state, value });
          store.update({units:value});
          //.props.updateparent(value);
          // .props.context.setState({units:value});
        }}
      />
  );
}

export {Units};

