import React, { Component } from 'react';
import * as utils from './utils.js';


class Workplane extends Component {
  constructor() {
    super();

    this.state = {
      v0: 0,
      v1: 0,
      v2: 500,
      v3: 100,
      newV2: 500,
      newV3: 100,
    }
  }

  wheel(e) {
    if (e.deltaY < 0) {
      console.log('scrolling up');
      let v2 = this.state.newV2;

      let newV2 = v2 + e.deltaY;

      this.setState({newV2});
    }
    if (e.deltaY > 0) {
      console.log('scrolling down');

      let v2 = this.state.newV2;

      let newV2 = v2 - e.deltaY;

      this.setState({newV2});
    }
    if (e.deltaX < 0) {
      console.log('scrolling left');
      let v3 = this.state.newV3;

      let newV3 = v3 + e.deltaY;

      this.setState({newV3});
    }
    if (e.deltaX > 0) {
      console.log('scrolling right');

      let v3 = this.state.newV3;

      let newV3 = v3 - e.deltaY;

      this.setState({newV3});
    }
  }

  render() {
    return (
      <svg
        viewBox={`${this.state.newV2} ${this.state.newV3} ${this.state.v2} ${this.state.v3}`}
        preserveAspectRatio={'slice'}
        xmlns={"http://www.w3.org/2000/svg"}
        onWheel={(e) => this.wheel(e)}
        style={{border: "1px solid #cccccc"}}>
        <circle cx="50" cy="50" r="50"/>
      </svg>
    );
  }
}

export {Workplane}
