import React, { Component } from 'react';
import './App.css';
import * as utils from './utils.js';
import * as mjs from 'makerjs';
import * as params from './parameters.js';
import * as s2p from 'shape2path';
import * as svgson from 'svgson';
import {Workplane} from './Workplane.js'
import Button from '@material-ui/core/Button';
import {kk} from './joints/koshikake.js';
import {profile} from './toolpaths/profile.js';
import {pocket} from './toolpaths/pocket.js';
//import {replaceWithPath} from './svg_to_makerjs.js';

class App extends Component {
  constructor() {
    super();

    this.state = {
      shapes: {},
      toolpaths:[],
      selected: [],
      material: "wood",
      thickness: 34,
      units: "mm",
      zero: "bottomleft"
    }
  };

  uploadSVG(e) {
    //helper function
    const replaceWithPath = (child) => { //why cant I put this in a different file
      if (child.name === "g") {
        return child.children.map(grandchild => replaceWithPath(grandchild));
      }

      if (child.name === "rectangle") {
        console.log(child);
        let temp = s2p.rect({x: 0, y: 120, width: 200, height: 200, rx: 20, ry: 20});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "path") {
        let makerjsPath = mjs.importer.fromSVGPathData(child.attributes.d);
        return makerjsPath;
      }
    }

    //main event

  }

  render() {
    //should I put everying in a list or all to one model, there seem to be some issues with origins if I mjs.exporter.toSVG() seperately
    let test = kk;
    //test = mjs.model.originate(test);
    let testProfile = test.models.profile;
    let testPocket = test.models.pocket;
    let profileCut = profile(testProfile, params);
    let pocketCut = pocket(testPocket, params);

    // let drawings = [test, profile(test)];
    // drawings = drawings.map(d => mjs.exporter.toSVG(d));

    //testing if internal outlines will make acceptable pocket paths, appears so
    //let p2 = pocket(testPocket, params).drawing;
    let t1 = new mjs.models.Oval(20,20)
    let t2 = mjs.model.move(new mjs.models.Oval(20,20), [15,0])
    let test0 = mjs.model.combineUnion(t1, t2);
    let test1 = mjs.model.outline(test0, 5, 1, true)
    // console.log(test0)
    // test0 = mjs.model.move(test0, [50, 0]);

    let model = {
      models: {
        shapes: {
          models: {
            profileSource: testProfile,
            pocketSource: testPocket,
          }
        },
        cuts: {
          models: {
            profileCut: profileCut.drawing,
            pocketCut: pocketCut.drawing,
          }
        }
        //pocketCut2: pocket(p2, params).drawing,
        // test0,
        // test1,
      }
    }

    model.models.cuts.layer = "red";

    // model = mjs.model.originate(model);
    // model = mjs.model.zero(model);

    model['paths'] = {
      origin: new mjs.paths.Circle(params.compensatedRadius)
    }

    let modelToView = mjs.model.scale(utils.deepcopy(model), 10); //TODO: make drawing easier to see, but this ruins toolpaths

    let viewModel = mjs.exporter.toSVG(modelToView, {
      useSvgPathOnly:false,
      strokeWidth:".5mm",
      // layerOptions:{
      //   cuts: {
      //     stroke: "red"
      //   }
      // },
    });
    //viewModel = viewModel.concat(Object.values(modelToView.models).map(m => mjs.exporter.toSVG(m)));
    viewModel = [viewModel];

    return (
      <div
        onMouseDown={(e) => console.log("div target", e.target.getAttribute("data-route"))}
        style={{
          margin: 0,
          height: "100%",
          overflow: "hidden"
        }}>

          <Workplane groups={viewModel}/>

        <div>
          <input
            id="myInput"
            type="file"
            ref={(ref) => this.upload = ref}
            style={{ display: 'none' }}
            onChange={(e) => this.uploadSVG(e)}/>
          <Button
            variant="contained"
            color="primary"
            onClick={(e) => this.upload.click()}
            >
              Upload SVG
          </Button>
          &nbsp;
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              utils.download(profileCut.filename, profileCut.gcode);
              utils.download(pocketCut.filename, pocketCut.gcode);
            }}
            >
              download gcode
          </Button>
        </div>

      </div>
    );
  }
}

export default App;
