import React from 'react';
import {TextInputField} from 'evergreen-ui';
import * as utils from './utils.js'


const testView = (valid) => {
  //let container = <div data-valid="true" id="whatever"></div>

  return (
    <TextInputField
        className="hippo"
        onChange={e => {
          let newValid = utils.isNum(e.target.value);
          if (utils.isNum(e.target.value) !== valid) {
            //replace TextInputField with testView(newValid)
            let el = document.getElementsByClassName("hippo")[0];
            var newEl = document.createElement('TextInputField');
            newEl.innerHTML = `${testView(newValid)}`;

            el.parentNode.replaceChild(newEl, el);

          }

          //what does this modify
        }}

        isInvalid={!(valid)}
        description={(valid) ? "" : "please enter a valid number"}
        label="testing"/>
  )
}

export {
  testView
}
