import React, { Component } from 'react';
import {Button, Dialog, TextInputField} from 'evergreen-ui';
import * as utils from '../utils.js';
import store from '../store.js';
import * as params from '../parameters.js';

class AdvancedOptions extends Component {

  state = {
    isShown: false,
    cutDepthValid: true,
    toolDiameterValid: true,
    stepoverPercentageValid: true,
    toleranceValid: true,
    jogHeightValid: true,
    jogRateValid: true,
    feedRateValid: true,
    parameters: params,
  }

  render() {

    // console.log(this.state.parameters);

    return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title="Advanced Default Settings"
          onCloseComplete={() => this.setState({isShown:false})}
          onConfirm={() => {
            let valid = this.state.cutDepthValid && this.state.toolDiameterValid &&
                        this.state.stepoverPercentageValid && this.state.toleranceValid &&
                        this.state.jogHeightValid && this.state.jogRateValid && this.state.feedRateValid;
            if (valid) {
              let cutDepth = document.getElementsByName("cutDepth")[0].value;
              let toolDiameter = document.getElementsByName("toolDiameter")[0].value;
              let stepoverPercentage = document.getElementsByName("stepoverPercentage")[0].value; //must be between 1 and 100%
              let tolerance = document.getElementsByName("tolerance")[0].value;
              let insideCutting = false;
              let jogHeight = document.getElementsByName("jogHeight")[0].value;
              let jogRate = document.getElementsByName("jogRate")[0].value;
              let feedRate = document.getElementsByName("feedRate")[0].value;

              //derived parameters
              stepoverPercentage = stepoverPercentage/100;
              let passDepth = cutDepth/2;
              let toolRadius = toolDiameter/2;
              let compensatedRadius = toolRadius + tolerance;
              let tempFloor = Math.ceil(cutDepth/passDepth); //should this be ceiling or floor, I think ceil
              let actualPassDepth = cutDepth/tempFloor;
              let depthOfPasses = utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);

              //settings will be compiled to a single object here
              // {
              //   compensatedRadius,
              //   feedRate,
              //   depthOfPasses,
              //   jogHeight,
              //   insideCutting,
              //   stepoverPercentage,
              //   jogRate
              // }

              this.setState({
                isShown:false,
              });

              //update app state here
              //this.props.updateparent()
              //store.update({})
            }
          }}>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({cutDepthValid:valid})
            }}
            isInvalid={!this.state.cutDepthValid}
            name="cutDepth"
            description={(this.state.cutDepthValid) ? "" : "please enter a valid number"}
            label="Depth of Cut"
            placeholder={this.state.parameters.cutDepth}/>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({toolDiameterValid:valid})
            }}
            isInvalid={!this.state.toolDiameterValid}
            name="toolDiameter"
            description={(this.state.toolDiameterValid) ? "" : "please enter a valid number"}
            label="Tool Diameter"
            placeholder={this.state.parameters.toolDiameter}/>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              valid = 0 < parseFloat(e.target.value) && parseFloat(e.target.value) <= 100;
              //process value here
              this.setState({stepoverPercentageValid:valid})
            }}
            isInvalid={!this.state.stepoverPercentageValid}
            name="stepoverPercentage"
            description={(this.state.stepoverPercentageValid) ? "" : "please enter a valid number"}
            label="Stepover Percentage"
            placeholder={this.state.parameters.stepoverPercentage}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({feedRateValid:valid})
              }}
              isInvalid={!this.state.feedRateValid}
              name="feedRate"
              description={(this.state.feedRateValid) ? "" : "please enter a valid number"}
              label="Feed Rate"
              placeholder={this.state.parameters.feedRate}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({toleranceValid:valid})
                }}
                isInvalid={!this.state.toleranceValid}
                name="tolerance"
                description={(this.state.toleranceValid) ? "" : "please enter a valid number"}
                label="Tolerance"
                placeholder={this.state.parameters.tolerance}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({jogRateValid:valid})
              }}
              isInvalid={!this.state.jogRateValid}
              name="jogRate"
              description={(this.state.jogRateValid) ? "" : "please enter a valid number"}
              label="Jog Rate"
              placeholder={this.state.parameters.jogRate}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({jogHeightValid:valid})
                }}
                isInvalid={!this.state.jogHeightValid}
                name="jogHeight"
                description={(this.state.jogHeightValid) ? "" : "please enter a valid number"}
                label="Jog Height"
                placeholder={this.state.parameters.jogHeight}/>

        </Dialog>
        <Button onClick={() => this.setState({isShown:true})}>Advanced Options</Button>
      </div>
    );
  }
}

export {AdvancedOptions};
