// makerjs model, params -> profile toolpath

import * as mjs from 'makerjs';

const profile = (model, params) => {
  //let p = model.models.profile;
  let p = model;

  let outline = mjs.model.outline(p, params.compensatedRadius, 1, params.insideCutting); //third parameter is what type of corners, 0 (rounded) by default
  // outline = mjs.model.zero(outline);
  // let chains = mjs.model.findChains(expansion);
  let chain = mjs.model.findSingleChain(outline);

  // console.log("outline", outline, "chain", chain);

  let divisions = Math.floor(chain.pathLength / params.minimumSpacing);
  let spacing = chain.pathLength / divisions;
  let keyPoints = mjs.chain.toKeyPoints(chain, spacing);

  // let scaleFactor = 10;
  // let scaleXY = keyPoints.map(p => [p[0] * scaleFactor, p[1] * scaleFactor]);

  let gcodePoints = keyPoints.map(p => `G1 X${p[0]} Y${p[1]} F${params.feedRate}`)
  //let gcode = JSON.parse(JSON.stringify(gcodePoints));

  let firstPoint = keyPoints[0];

  let paths = params.depthOfPasses.map((p,i) => [`G1 Z${params.depthOfPasses[i]} F12.0`, ...gcodePoints, `G1 X${firstPoint[0]} Y${firstPoint[1]} F${params.feedRate}`])
  paths = paths.flat(1);

  let gcode = [
    params.units, //units
    "G90",
    `G1 Z${params.jogHeight} F9.0`,
    `G0 X${firstPoint[0]} Y${firstPoint[1]}`,
    ...paths,
    `G1 Z${params.jogHeight} F9.0`,
  ]

  // write to a new file named
  let filename = 'testGcodeProfile.nc';

  let text = gcode.join('\n');

  // console.log("profile", outline)

  return {drawing: outline, gcode: text, filename};
};


export {profile};
