//This code is adapted from https://github.com/makercam/makercam/blob/master/src/clipperOffset.ts

import ClipperLib from 'js-clipper';

const EndTypes = {
    etOpenSquare: 0,
    etOpenRound: 1,
    etOpenButt: 2,
    etClosedPolygon: 3,
    etClosedLine: 4
};

const JoinTypes = [
    ClipperLib.JoinType.jtSquare,
    ClipperLib.JoinType.jtRound,
    ClipperLib.JoinType.jtMiter
]

export const clipOffset = (contour, offset, joints = 0, tolerance = 0.01) => {
    const scale = 1000
    console.log(paths)
    let paths = contour.map(line => ([
                        {X: Math.round(line.origin[0] * scale), Y: Math.round(line.origin[1] * scale)},
                        {X: Math.round(line.end[0] * scale), Y: Math.round(line.end[1] * scale)}
                ]));

    console.log(paths);

    const co = new ClipperLib.ClipperOffset()
    const offsetted = new ClipperLib.Paths()
    co.Clear()
    co.AddPaths(paths, JoinTypes[joints], EndTypes.etClosedLine) //chain.endless ? EndTypes.etClosedLine : EndTypes.etOpenButt
    co.MiterLimit = 2
    co.ArcTolerance = 0.25
    co.Execute(offsetted, offset * scale);

    console.log(offsetted);
}
