import React, { Component } from 'react';
import * as utils from './utils.js';


class Workplane extends Component {
  constructor() {
    super();

    this.state = {
      width: 500,
      height: 500,
      v0: 0,
      v1: 0,
      v2: 500,
      v3: 500,
      mousedown: false,
      //zoomDelta: 0,
    }
  }

  wheel(e) {
    e.preventDefault();
    let loc = utils.getSVGpoint(e, this.state.v0, this.state.v1, this.state.v2, this.state.v3 ,this.state.width, this.state.height);

    if (e.ctrlKey) {

      let zoomRate = 10;

      if (e.deltaY < 0) {
        //console.log('zoom in');

        let v2 = (e.deltaY * zoomRate + this.state.v2 > 1) ? e.deltaY * zoomRate + this.state.v2 : this.state.v2;
        let v3 = (e.deltaY * zoomRate + this.state.v3 > 1) ? e.deltaY * zoomRate + this.state.v3 : this.state.v3;
        //
        // let v0 = (this.state.v2 - v2) / 2;
        // let v1 = (this.state.v3 - v3) / 2;

        this.setState({v2, v3})
      }

      if (e.deltaY > 0) {
        //console.log('zoom out');

        let v2 = e.deltaY * zoomRate + this.state.v2;
        let v3 = e.deltaY * zoomRate + this.state.v3;

        // let v0 = (this.state.v2 - v2) / 2;
        // let v1 = (this.state.v3 - v3) / 2;

        this.setState({v2, v3})
      }

      return
    }

    if (e.deltaY < 0) {
      //console.log('scrolling up');

      let v1 = this.state.v1 + e.deltaY;
      this.setState({v1});
    }
    if (e.deltaY > 0) {
      //console.log('scrolling down');

      let v1 = this.state.v1 + e.deltaY;
      this.setState({v1});
    }
    if (e.deltaX < 0) {
      //console.log('scrolling left');

      let v0 = this.state.v0 + e.deltaX;
      this.setState({v0});
    }
    if (e.deltaX > 0) {
      //console.log('scrolling right');

      let v0 = this.state.v0 + e.deltaX;
      this.setState({v0});
    }
  }

//   // Find your root SVG element
// var svg = document.querySelector('svg');
//
// // Create an SVGPoint for future math
// var pt = svg.createSVGPoint();
//
// // Get point in global SVG space
// function cursorPoint(evt){
//   pt.x = evt.clientX; pt.y = evt.clientY;
//   return pt.matrixTransform(svg.getScreenCTM().inverse());
// }
//
// svg.addEventListener('mousemove',function(evt){
//   var loc = cursorPoint(evt);
//   // Use loc.x and loc.y here
// },false);

  mouseDown(e) {
    this.setState({
      mousedown: true
    });
  }

  mouseMove(e) {
    let loc = utils.getSVGpoint(e, this.state.v0, this.state.v1, this.state.v2, this.state.v3 ,this.state.width, this.state.height);

    // console.log(loc.x, loc.y)
  }

  mouseUp(e) {
    this.setState({
      mousedown: false
    });
  }

  render() {

    return (
      <svg
        width={this.state.width}
        height={this.state.height}
        viewBox={`${this.state.v0} ${this.state.v1} ${this.state.v2} ${this.state.v3}`}
        xmlns={"http://www.w3.org/2000/svg"}
        onWheel={(e) => this.wheel(e)}
        onMouseDown={(e) => this.mouseDown(e)}
        onMouseMove={(e) => this.mouseMove(e)}
        onMouseUp={(e) => this.mouseUp(e)}
        style={{border: "1px solid #cccccc"}}>

          {this.props.groups.map(g => <g dangerouslySetInnerHTML={{__html: g}}/>)}
      </svg>
    );
  }
}

export {Workplane}
