import React, { Component, PureComponent } from 'react';
import {Button, Heading, Pane, Paragraph} from 'evergreen-ui';
import './App.css';

import * as utils from './utils.js';
import * as mjs from 'makerjs';
import * as params from './parameters.js';
import * as s2p from 'shape2path';
import * as svgson from 'svgson';

import {uploadSVG} from './uploadSVG.js';

import {Workplane} from './Workplane.js'
import {MaterialType} from './components/MaterialType.js';
import {MaterialThickness} from './components/MaterialThickness.js';
import {Origin} from './components/Origin.js';
import {Units} from './components/Units.js';
import {PocketButton} from './components/PocketButton.js';
import {ProfileButton} from './components/ProfileButton.js';
import {AdvancedOptions} from './components/AdvancedOptions.js';
import {ToolpathList} from './components/ToolpathList.js';

import {renderLine} from './unmakerjs/primitives.js';

import store from "./store.js"; //this is my global state library, this is the only file that will getState() it will pass that to all sub components or should i import store in respective components

class renderLineGroup extends Component {
  state = {
    highlight: false
  }

  toggleHighlight = () => {
    let highlight = !this.state.highlight;

    this.setState({highlight})
  }

  render() {
    return <g
            onMouseEnter={() => console.log("hi")}
            onMouseLeave={() => console.log("bye")}
            >
              {this.props.lines.map((line, i) => renderLine(line, `${this.props.rootID}_index${i}`, false, true))}
           </g>
  }
}

class App extends Component {

  state = {
    thicknessSubmit : false,
    materialSubmit: false,
    originSubmit: false,
    initialized: true, //will be false
  }

  firstSubmit = (name) => {
    let old = this.state;
    old[name] = true;

    this.setState({old});
  }

  initialized() {
    if (!this.state.initialized && (this.state.thicknessSubmit && this.state.materialSubmit && this.state.originSubmit)) {
      this.setState({initialized:true});
    }
  }

  hover = (dataRoute, id) => {

    console.log("id", id);

    if (dataRoute !== null) {
      if (dataRoute.split(",").length < 4) {return} // TODO: This is a bit of a hack

      dataRoute = dataRoute.split(",")[3].replace(/[\"]/g, "");

      let id = dataRoute.split("_");
      id = (id.length > 1) ? id[1] : id[0];

      store.update({ hovered: [id] });

    } else {
      store.update({ hovered: [] }); //this is a bit hackish
    }
  }

  select = (dataRoute, id) => {
    console.log("selected id", id);

    if (dataRoute === null) {
      store.update({selected:[]});
    }

    else { //TODO this is when hover is turned off
      if (dataRoute.split(",").length < 4) {return} // TODO: This is a bit of a hack

      dataRoute = dataRoute.split(",")[3].replace(/[\"]/g, "");

      let id = dataRoute.split("_");
      id = (id.length > 1) ? id[1] : id[0];

      store.update(s => s.selected.push(id));
    }
  }

  // componentDidMount() { //tests
  //   let modelT = {
  //     paths: {
  //       0: new mjs.paths.Line([0, 0], [10, 10]),
  //       1: new mjs.paths.Line([0, 0], [20, 10]),
  //       2: new mjs.paths.Line([0, -20], [0, 0]),
  //       3: new mjs.paths.Line([20, 10], [30, 10]),
  //       4: new mjs.paths.Line([0, -20], [-10, 0]),
  //     }
  //   };
  //
  //   let chainsT = mjs.model.findChains(modelT);
  //
  //   let chainsTModels = chainsT.map(chain => mjs.chain.toNewModel(chain));
  //
  //   console.log("chainsT", chainsT);
  //   console.log("chainsTModels", chainsTModels);
  //
  //   let shapes = store.state.shapes;
  //
  //   shapes["paths"] = modelT.paths;
  //
  //   store.update({shapes})
  // }

  render() {
    //should I put everying in a list or all to one model, there seem to be some issues with origins if I mjs.exporter.toSVG() seperately
    if (!this.state.initialized) this.initialized();

    let drawing = {
      models: {
        shapes: {
          models: {
            ...store.getState().shapes
          }
        },
        cuts: {
          models: {}
        },
        selected: {
          models: {}
        },
        hovered: {
          models: {}
        }
      }
    }

    store.getState().toolpaths.forEach(path => {
      if (path.cutSelected) {
        drawing.models.cuts.models[path.id] = path.geometry.drawing;
      }
    })

    store.getState().selected.forEach(shape => {
      drawing.models.selected.models[`selected_${shape}`] = store.getState().shapes[shape];
    })

    store.getState().hovered.forEach(shape => {
      drawing.models.hovered.models[`hovered_${shape}`] = store.getState().shapes[shape];
    })

    drawing.models.cuts.layer = "red";
    drawing.models.selected.layer = "blue";
    drawing.models.hovered.layer = "yellow";


    let viewModel = mjs.exporter.toSVG(drawing, {
      useSvgPathOnly:false,
      strokeWidth:".5mm",
    });

    viewModel = [viewModel];

    let viewModel2 = Object.entries(store.state.contours).
                      map(([id, lines]) => <renderLineGroup
                                              key={`${id}_group`}
                                              rootid={id}
                                              lines={lines}
                                            />);

    return (
      <div
        className="wrapper"
        >


        <div>
          <div
            onMouseMove={(e) => this.hover(e.target.getAttribute("data-route"), e.target.id)}
            onMouseDown={(e) => this.select(e.target.getAttribute("data-route"), e.target.id)}
            className="mainEvent">
            <Workplane groups={viewModel}/>
            <svg width={0} height={0} id={"thisIsDumb"}></svg>
          </div>
          <Workplane groups={[]} groups2={viewModel2}/>

          <div>
            <input
              id="myInput"
              type="file"
              ref={(ref) => this.upload = ref}
              style={{ display: 'none' }}
              onChange={uploadSVG}/>
            <Button
              marginRight={4}
              onClick={(e) => this.upload.click()}>
                upload (SVG, DXF, BBJS, or Makerjs)
            </Button>
            <Button
              onClick={() => console.log(store.state)}>
              save
            </Button>
            <br/>
            Number Selected: {store.state.selected.length}
            &nbsp;
            <Button
              onClick={() => {
                let shapes = Object.entries(store.state.shapes);
                shapes = shapes.filter(([id, shape]) => !store.state.selected.includes(id))

                let toolpaths = store.state.toolpaths.filter(toolpath => !store.state.selected.includes(toolpath.sourceGeometryID));

                let newShapes = {};

                shapes.forEach(shape => {
                  newShapes[shape[0]] = shape[1];
                })

                shapes = newShapes;

                store.update({
                  shapes,
                  toolpaths,
                  selected: []
                })

              }}>
              Delete
            </Button>
          </div>
        </div>

        <div className="sidebar">
          <div>
            <Heading>Settings</Heading>
            <Pane marginTop={4}> <MaterialType firstSubmit={this.firstSubmit}/></Pane>
            <Pane marginTop={4}> <MaterialThickness firstSubmit={this.firstSubmit}/></Pane>
            <Pane marginTop={4}> <Units/></Pane>
            <Pane marginTop={4}> <Origin firstSubmit={this.firstSubmit}/></Pane>
            {(!this.state.initialized) ? <Paragraph marginTop={4}>select settings before editting options</Paragraph> : ""}
            <Pane> <AdvancedOptions disabled={!this.state.initialized} default={true} params={store.getState().defaultParameters}/></Pane>
          </div>

          <div>
            <Heading marginTop={8}>Create Toolpaths</Heading>
            {(!this.state.initialized) ? <Paragraph marginTop={4}>select settings before creating toolpaths</Paragraph> : ""}
            <div className="toolpaths">
              <Pane marginLeft={8} marginTop={4}><ProfileButton disabled={!this.state.initialized}/></Pane>
              <Pane marginLeft={8} marginTop={4}><PocketButton disabled={!this.state.initialized}/></Pane>
              <Button disabled={!this.state.initialized} marginLeft={8} marginTop={4} onClick={() => console.log(store.getState())}>Drill</Button>
            </div>
          </div>

          <div>
            <Heading marginTop={8}>Toolpaths</Heading>
            <ToolpathList marginTop={4} state={store.getState()}/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
