import React, { Component } from 'react';
import {Button, Dialog, TextInput} from 'evergreen-ui';
import * as utils from '../utils.js';

class MaterialThickness extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isShown: false,
      value: "set material thickness",
      valid: true,
    }
  }

  render() {

    return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title="Material Thickness"
          onCloseComplete={() => this.setState({isShown:false})}
          onConfirm={() => {
            let value = (this.state.valid) ? document.getElementsByName("thickness_input")[0].value : this.state.value;

            this.setState({
              isShown:false,
              value
            });
          }}>

          <TextInput
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({valid})
            }}
            isInvalid={this.state.valid}
            name="thickness_input"
            placeholder={this.state.value}
          />
        </Dialog>
        <Button onClick={() => this.setState({isShown:true})}>{`Thickness: ${this.state.value}`}</Button>
      </div>
    );
  }
}

export {MaterialThickness};
