// these are the default settings

import * as utils from './utils.js';

//name
let name = "anon"

//sampling rate of gcode points, not used when taking keyPoints
let minimumSpacing = 2;

//units
let units = "mm"

//generate gcode data
let cutDepth = 8.5;
let toolDiameter = 4;
let passDepth = .3; //toolDiameter/2;

//let numberOfOffsets = 3;
//let stepover = 2;
let stepoverPercentage = 60;
// stepoverPercentage = stepoverPercentage/100;

let tolerance = 0;

//let climbDirection = false;
let offset = "outside";
//let millOuterFirst = false;

let jogHeight = 10;
let jogRate = 6000;
let feedRate = 2200;
let plungeRate = 1000

//derived parameters
let toolRadius = toolDiameter/2;
//let nSeries = toolDiameter * stepover;
let compensatedRadius = toolRadius + tolerance;

let tabs = false;
let tabThickness = 0;
let dogbone = false;

export const depthOfPasses = (cutDepthArg, passDepthArg) => {
  let tempFloor = Math.ceil(cutDepthArg/passDepthArg); //should this be ceiling or floor, I think ceil
  let actualPassDepth = cutDepthArg/tempFloor;

  return utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);
};

export const params = {
  name,
  dogbone,
  tabThickness,
  tabs,
  tolerance,
  toolDiameter,
  compensatedRadius: toolDiameter /2 + tolerance,
  cutDepth,
  passDepth,
  jogHeight,
  minimumSpacing,
  offset,
  stepoverPercentage,
  feedRate,
  jogRate,
  units,
  plungeRate,
}
