// makerjs model, depth, params -> pocket toolpath
import * as mjs from 'makerjs';

const STRANGE_CORRECTION = 0.0000001; //TODO: why is this neccessary otherwise outline shits the bed

const len = (toolpath) => Object.keys(toolpath.models).length;

const isToolpath = (toolpath) => {
  //console.log("toolpath", toolpath);

  if (len(toolpath) === 1) {
    return true;
  } else if (len(toolpath) > 1) {
    return true;
  } else {
    return false;
  }
}

const intToolpath = (geo, radius, corners = 1) => {
  // console.log("geo", geo);
  // console.log(len(geo));
  // let corners = (len(geo) === 1) ? 1 : 0;

  return mjs.model.outline(geo, radius + STRANGE_CORRECTION, corners, true);
}

const intToolpaths = (geo, params) => {
  let pass = intToolpath(geo, params.compensatedRadius);

  if (isToolpath(pass) !== true) {
    return {models:{}};
  }

  let models = {}
  let count = 0;
  let corners = 1;

  while ( isToolpath(pass)) {
    models[count] = pass;
    count++;

    // this is a strange issue
    // not entirely sure why mjs has issues with some corner parameters when there are sharp separate corners in model
    try {
      pass = intToolpath(models[count - 1], params.compensatedRadius * params.stepoverPercentage, corners);
    } catch {
      corners = 0;
    }
    //
    // if (len(pass) > 1) {
    //   corners = 0;
    // }

    //console.log("pass", pass, "count", count, "ispath", isToolpath(pass));
  }

  let model = {models}

  //console.log("model", model)

  return model;
}

const pocket = (model, params) => {
  //let p = model.models.profile;
  let p = model;

  // let outline = mjs.model.outline(p, params.compensatedRadius, 1, true); //third parameter is what type of corners, 0 (rounded) by default
  // let outline = intToolpath(p, params.compensatedRadius);
  let outlines = intToolpaths(p, params);
  let iterOut = Object.values(outlines.models);

  // let chains = mjs.model.findChains(outline);
  // let chain = mjs.model.findSingleChain(outline);
  // let chain2 = iterOut.map(x => mjs.model.findSingleChain(x)); //TODO: need to change this to find chains
  let chains2 = iterOut.map(x => mjs.model.findChains(x));
  console.log(chains2)
  //chains2 = chains2.flat(1);

  var divisions = chains2.map(c => c.map(c2 => Math.floor(c2.pathLength / params.minimumSpacing)));
  var spacing = chains2.map((c,i) => c.map((c2, j) => c2.pathLength / divisions[i][j]));

  // let keyPoints = mjs.chain.toKeyPoints(chain, spacing);
  let keyPoints2 = chains2.map((c, i) => c.map((c2, j) => mjs.chain.toKeyPoints(c2, spacing[i][j])));
  // console.log(keyPoints2);

  // keyPoints2 = keyPoints2.flat(1);

  // let scaleFactor = 10;
  // let scaleXY = keyPoints.map(p => [p[0] * scaleFactor, p[1] * scaleFactor]);

  // let gcodePoints = keyPoints.map(p => `G1 X${p[0]} Y${p[1]} F${params.feedRate}`)
  let gcodePoints2 = keyPoints2.map(arr => {
      return arr.map(line => `G1 X${line[0]} Y${line[1]} F${params.feedRate}`)
  });
  // console.log(gcodePoints2)
  gcodePoints2 = gcodePoints2.flat(1);

  // let gcode = JSON.parse(JSON.stringify(gcodePoints));
  // let gcode2 = JSON.parse(JSON.stringify(gcodePoints2));

  //console.log(gcode2);

  // let firstPoint = keyPoints[0];
  let firstPoints2 = keyPoints2.map(x => x[0]);
  // console.log(firstPoints2)

  // let paths = params.depthOfPasses.map((p,i) => [
  //   `G1 Z${params.depthOfPasses[i]} F12.0`,
  //   ...gcodePoints,
  //   `G1 X${firstPoint[0]} Y${firstPoint[1]} F${params.feedRate}`
  // ])
  // paths = paths.flat(1);

  let paths2 = gcodePoints2.map((p, i) => [
    ...p,
    `G1 X${firstPoints2[i][0]} Y${firstPoints2[i][1]} F${params.feedRate}`
  ])
  paths2 = paths2.flat(1);
  //console.log(paths2)

  let paths3 = params.depthOfPasses.map((p,i) => [
    `G1 Z${params.depthOfPasses[i]} F12.0`,
    ...paths2,
  ])
  paths3 = paths3.flat(1);

  // console.log(paths3)

  // let gcode = [
  //   params.units,
  //   "G90",
  //   `G1 Z${params.jogHeight} F9.0`,
  //   `G0 X${firstPoint[0]} Y${firstPoint[1]}`,
  //   ...paths
  // ]

  let gcode2 = [
    params.units,
    "G90",
    `G1 Z${params.jogHeight} F9.0`,
    `G0 X${firstPoints2[0][0]} Y${firstPoints2[0][1]}`,
    ...paths3,
    `G1 Z${params.jogHeight} F9.0`,
  ]

  // write to a new file named
  let filename = 'testGcodePocket.nc';

  let text = gcode2.join('\n');

  return {drawing: outlines, gcode: text, filename};
};


export {pocket};
