// these are the default settings

import * as utils from './utils.js';

//name
let name = "anon"

//sampling rate of gcode points, not used when taking keyPoints
let minimumSpacing = 2;

//units
let units = "mm"

//generate gcode data
let cutDepth = 23;
let toolDiameter = 6;
let passDepth = toolDiameter/2;

//let numberOfOffsets = 3;
//let stepover = 2;
let stepoverPercentage = 60;
// stepoverPercentage = stepoverPercentage/100;

let tolerance = 0;

//let climbDirection = false;
let offset = "outside";
//let millOuterFirst = false;

let jogHeight = 10;
let jogRate = 6000;
let feedRate = 2200;
let plungeRate = 1000

//derived parameters
let toolRadius = toolDiameter/2;
//let nSeries = toolDiameter * stepover;
let compensatedRadius = toolRadius + tolerance;

let tempFloor = Math.ceil(cutDepth/passDepth); //should this be ceiling or floor, I think ceil
let actualPassDepth = cutDepth/tempFloor;

let depthOfPasses = utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);

let tabs = false;
let tabThickness = 0;
let dogbone = false;

export {
  name,
  dogbone,
  tabThickness,
  tabs,
  tolerance,
  toolDiameter,
  cutDepth,
  compensatedRadius,
  feedRate,
  depthOfPasses,
  jogHeight,
  minimumSpacing,
  offset,
  stepoverPercentage,
  jogRate,
  units
}
