// makerjs model, depth, params -> pocket toolpath
import * as mjs from 'makerjs';
import clipperOffset from './clipperOffset.js';
import {clipOffset} from '../unmakerjs/clipper.js'


const len = (toolpath) => Object.keys(toolpath).length;

const isToolpath = (toolpath) => {
  // console.log("toolpath", toolpath);

  if (len(toolpath) >= 1) {
    return true;
  } else {
    return false;
  }
}

const intToolpaths = (geo, params) => {
  let contours = {};
  let count = 0;

  let pass = clipOffset(geo, -params.compensatedRadius);

  if (isToolpath(pass) !== true) {
    return contours;
  }

  while ( isToolpath(pass)) {
    contours[count] = pass;
    count++;
    pass = clipOffset(contours[count - 1], -params.compensatedRadius * params.stepoverPercentage/100);
  }

  return contours;
}

const pocket = (contour, params) => {

  let outlines = intToolpaths(contour, params);

  let keyPoints = Object.values(outlines).map(outline => outline.map(line => [line.origin[0], line.origin[1]]));

  outlines = Object.values(outlines).flat();

  console.log("keyPoints", keyPoints);

  let gcodePoints = keyPoints.map(points => {
    let raise = c.length > 1;
    let firstPoint = points[0];

    firstPoint = `G1 X${firstPoint[0]} Y${firstPoint[1]} F${params.feedRate}`;

    if (raise) {
      return [
        `G1 Z${params.jogHeight} F${params.jogRate}`,
        firstPoint, //want to jog to this one
        "plunge",
        ...c2.map(c3 => `G1 X${c3[0]} Y${c3[1]} F${params.feedRate}`),
        firstPoint,
        `G1 Z${params.jogHeight} F${params.jogRate}`,
      ]
    } else {
      return [
        firstPoint,
        "plunge",
        ...c2.map(c3 => `G1 X${c3[0]} Y${c3[1]} F${params.feedRate}`),
        firstPoint
      ]
    }
  });

  let paths = gcodePoints.flat();

  // let firstPoint = keyPoints[0][0][0];

  let paths2 = params.depthOfPasses.map((p,i) => [
    ...paths.flat(1).map(x => (x === "plunge") ? `G1 Z${params.depthOfPasses[i]} F12.0` : x),
  ])

  // console.log(paths2)

  paths2 = paths2.flat(1);

  let preamble = [params.units, "G90"];

  let gcode = [
    ...preamble,
    "(end of preamble)",
    `G1 Z${params.jogHeight} F${params.jogRate}`,
    // `G0 X${firstPoint[0]} Y${firstPoint[1]}`,
    ...paths2,
    `G1 Z${params.jogHeight} F${params.jogRate}`,
  ]

  // write to a new file named
  let filename = 'testGcodePocket.gcode';

  let text = gcode.join('\n');

  return {drawing: outlines, gcode: text, filename};
};


export {pocket};
