import React, { Component, PureComponent } from 'react';
import {Button, Dialog, TextInputField, SegmentedControl, Heading, Link, Strong, Text} from 'evergreen-ui';
import * as utils from '../utils.js';
import store from '../store.js';
import {profile} from '../toolpaths/profile.js';
import {pocket} from '../toolpaths/pocket.js';

class AdvancedOptions extends Component {

  state = {
    isShown: false,
    nameValid: true,
    cutDepthValid: true,
    toolDiameterValid: true,
    stepoverPercentageValid: true,
    toleranceValid: true,
    jogHeightValid: true,
    jogRateValid: true,
    feedRateValid: true,
    tabThicknessValid: true,
    offsetOptions: [
      { label: 'outside', value: "outside" },
      { label: 'inside', value: "inside" },
      { label: 'none', value: "none"}
    ],
    tabOptions: [
      { label: 'true', value: true },
      { label: 'false', value: false },
    ],
    dogboneOptions: [
      { label: 'true', value: true },
      { label: 'false', value: false },
    ],
    offsetTemp: this.props.params.offset,
    tabsTemp: this.props.params.tabs,
    dogboneTemp: this.props.params.dogbone,
  }

  validateSubmit = () => {

    let valid = this.state.nameValid &&
                this.state.cutDepthValid &&
                this.state.toolDiameterValid &&
                this.state.stepoverPercentageValid &&
                this.state.toleranceValid &&
                this.state.jogHeightValid &&
                this.state.jogRateValid &&
                this.state.feedRateValid;
    if (valid) {

      let cutDepth = Number(utils.helpExtract("cutDepth"));
      let toolDiameter = Number(utils.helpExtract("toolDiameter"));
      let stepoverPercentage = Number(utils.helpExtract("stepoverPercentage")); //must be between 1 and 100%
      let tolerance = Number(utils.helpExtract("tolerance"));
      let jogHeight = Number(utils.helpExtract("jogHeight"));
      let jogRate = Number(utils.helpExtract("jogRate"));
      let feedRate = Number(utils.helpExtract("feedRate"));
      let tabThickness = Number(utils.helpExtract("tabThickness"));

      if (utils.helpExtract("cutDepth") === "") cutDepth = this.props.params.cutDepth;
      if (utils.helpExtract("toolDiameter") === "") toolDiameter = this.props.params.toolDiameter;
      if (utils.helpExtract("stepoverPercentage") === "") stepoverPercentage = this.props.params.stepoverPercentage; //must be between 1 and 100%
      if (utils.helpExtract("tolerance") === "") tolerance = this.props.params.tolerance;
      if (utils.helpExtract("jogHeight") === "") jogHeight = this.props.params.jogHeight;
      if (utils.helpExtract("jogRate") === "") jogRate = this.props.params.jogRate;
      if (utils.helpExtract("feedRate") === "") feedRate = this.props.params.feedRate;
      if (utils.helpExtract("tabThickness") === "") tabThickness = this.props.params.tabThickness;

      let dogbone = this.state.dogboneTemp;
      let tabs = this.state.tabsTemp;
      let offset = this.state.offsetTemp;

      //derived parameters
      let passDepth = cutDepth/2;
      let toolRadius = toolDiameter/2;
      let compensatedRadius = toolRadius + tolerance;
      let tempFloor = Math.ceil(cutDepth/passDepth); //should this be ceiling or floor, I think ceil
      let actualPassDepth = cutDepth/tempFloor;
      let depthOfPasses = utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n);

      //settings will be compiled to a single object here
      let parameters = utils.deepcopy(this.props.params);

      parameters["dogbone"] = dogbone;
      parameters["tabs"] = tabs;
      parameters["offset"] = offset;
      parameters["tolerance"] = tolerance;
      parameters["tabThickness"] = tabThickness;
      parameters["toolDiameter"] = toolDiameter;
      parameters["compensatedRadius"] = compensatedRadius;
      parameters["cutDepth"] = cutDepth;
      parameters["depthOfPasses"] = depthOfPasses;
      parameters["feedRate"] = feedRate;
      parameters["jogHeight"] = jogHeight;
      parameters["jogRate"] = jogRate;
      // parameters["insideCutting"] = insideCutting;
      parameters["stepoverPercentage"] = stepoverPercentage;

      //console.log("parameters", parameters);

      this.setState({
        isShown:false,
      });

      //update app state here
      // if this.props.default === true then update defaultParameters state, if false then update parameters of toolpath id
      if (this.props.default) {
        console.log("update default parameters", parameters)
        store.update({defaultParameters:parameters});
      } else {
        let state = store.getState();
        let toolpaths = state.toolpaths;
        let path = toolpaths[this.props.index];

        path.parameters = parameters;
        let sourceID = path.sourceGeometryID;
        let sourceGeo = state.shapes[sourceID];

        if (path.type === "profile") {
          let cutGeo = profile(sourceGeo, parameters);
          path.geometry = cutGeo;
        }

        if (path.type === "pocket") {
          let cutGeo = pocket(sourceGeo, parameters);
          path.geometry = cutGeo;
        }

        if (path.type === "drill") {
          //console.log("ya got to wait for that feature") //TODO
        }

        store.update({toolpaths});
      }
    }
  }

  render() {

    // console.log(this.props.params);

    return (
      <div>
        <Dialog
          isShown={this.state.isShown}
          title={this.props.default ? "Advanced Default Settings" : "Specific Toolpath Settings"}
          onCloseComplete={() => {
            this.setState({
              isShown:false,
              dogboneTemp: this.props.params.dogbone,
              tabsTemp: this.props.params.tabs,
              offsetTemp: this.props.params.offset,
            })
          }}
          onConfirm={this.validateSubmit}>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({cutDepthValid:valid})
            }}
            isInvalid={!this.state.cutDepthValid}
            name="cutDepth"
            description={(this.state.cutDepthValid) ? "" : "please enter a valid number"}
            label="Depth of Cut"
            placeholder={String(this.props.params.cutDepth)}/>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              this.setState({toolDiameterValid:valid})
            }}
            isInvalid={!this.state.toolDiameterValid}
            name="toolDiameter"
            description={(this.state.toolDiameterValid) ? "" : "please enter a valid number"}
            label="Tool Diameter"
            placeholder={String(this.props.params.toolDiameter)}/>

          <TextInputField
            onChange={e => {
              let valid = utils.isNum(e.target.value);
              valid = 0 < Number(e.target.value) && Number(e.target.value) <= 100;
              //process value here
              this.setState({stepoverPercentageValid:valid})
            }}
            isInvalid={!this.state.stepoverPercentageValid}
            name="stepoverPercentage"
            description={(this.state.stepoverPercentageValid) ? "" : "please enter a valid number"}
            label="Stepover Percentage (this is only relevant for pocket cuts)"
            placeholder={String(this.props.params.stepoverPercentage)}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({feedRateValid:valid})
              }}
              isInvalid={!this.state.feedRateValid}
              name="feedRate"
              description={(this.state.feedRateValid) ? "" : "please enter a valid number"}
              label="Feed Rate"
              placeholder={String(this.props.params.feedRate)}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({toleranceValid:valid})
                }}
                isInvalid={!this.state.toleranceValid}
                name="tolerance"
                description={(this.state.toleranceValid) ? "" : "please enter a valid number"}
                label="Tolerance"
                placeholder={String(this.props.params.tolerance)}/>

            <TextInputField
              onChange={e => {
                let valid = utils.isNum(e.target.value);
                this.setState({jogRateValid:valid})
              }}
              isInvalid={!this.state.jogRateValid}
              name="jogRate"
              description={(this.state.jogRateValid) ? "" : "please enter a valid number"}
              label="Jog Rate"
              placeholder={String(this.props.params.jogRate)}/>

              <TextInputField
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({jogHeightValid:valid})
                }}
                isInvalid={!this.state.jogHeightValid}
                name="jogHeight"
                description={(this.state.jogHeightValid) ? "" : "please enter a valid number"}
                label="Jog Height"
                placeholder={String(this.props.params.jogHeight)}/>

              <Heading marginTop={8} size={400} color={"rgba(67, 90, 111, .91)"}>Offset Direction (this is only relevant for profile cuts)</Heading>
              <SegmentedControl
                marginTop={4}
                options={this.state.offsetOptions}
                value={this.state.offsetTemp}
                onChange={value => {
                  let offsetTemp = value;
                  this.setState({ offsetTemp });
                }}
              />

              <Heading marginTop={20} size={400} color={"rgba(67, 90, 111, .91)"}>Auto Tabs (this is only relevant for profile cuts)</Heading>
              <SegmentedControl
                marginTop={4}
                options={this.state.tabOptions}
                value={this.state.tabsTemp}
                onChange={value => {
                  let tabsTemp = value;
                  this.setState({ tabsTemp });
                }}
              />

              <TextInputField
                marginTop={20}
                onChange={e => {
                  let valid = utils.isNum(e.target.value);
                  this.setState({tabThicknessValid:valid})
                }}
                isInvalid={!this.state.tabThicknessValid}
                name="tabThickness"
                description={(this.state.tabThicknessValid) ? "" : "please enter a valid number"}
                label="Tab Thickness"
                placeholder={String(this.props.params.tabThickness)}/>

              <Heading marginTop={20} size={400} color={"rgba(67, 90, 111, .91)"}>Auto Dogbone</Heading>
              <SegmentedControl
                marginTop={4}
                options={this.state.dogboneOptions}
                value={this.state.dogboneTemp}
                onChange={value => {
                  let dogboneTemp = value;
                  this.setState({ dogboneTemp });
                }}
              />

        </Dialog>
        <Link onClick={() => this.setState({isShown:!this.props.disabled})}>{this.props.default ? "Advanced Default Options" : "edit"}</Link>
      </div>
    );
  }
}

export {AdvancedOptions};
