import React from 'react';
import {TextInputField} from 'evergreen-ui';
import * as utils from './utils.js'


const testView = () => {
  //let container = <div data-valid="true" id="whatever"></div>

  return (
    <div data-valid={"true"} id={"content"}>
      <TextInputField

        onChange={e => {
          let newValid = utils.isNum(e.target.value);

          document.getElementById("content").setAttribute("data-valid", String(newValid))

        }}

        isInvalid={!(document.getElementById("content").getAttribute("data-valid") === "true")}
        description={(document.getElementById("content").getAttribute("data-valid") === "true") ? "" : "please enter a valid number"}
        label="testing"/>
    </div>
  )
}

export {
  testView
}
