import * as mjs from 'makerjs';
import * as s2p from 'shape2path';
import * as svgson from 'svgson';
import * as utils from './utils.js';
import {flattenSVG} from 'flatten-svg';

import {connectPoints} from './unmakerjs/primitives.js';
import {clipOffset} from './unmakerjs/clipper.js'



import store from "./store.js"; //this is my global state library, this is the only file that will getState() it will pass that to all sub components or should i import store in respective components


const uploadSVG = (e) => {

  //main event
  let files = e.target.files;
  let file = files[0];

  var reader = new FileReader();

  reader.onload = (event) => {
    let text = event.target.result;

    let hmm = document.getElementById('thisIsDumb');
    hmm.innerHTML = text;

    let paths = flattenSVG(hmm);

    let models2 = {};
    let contour; //testing

    Object.values(paths).forEach((path, i) => {
      let lines2 = connectPoints(path.points);
      contour = lines2 //testing
      models2[utils.makeID()] = lines2;
    })

    let shapes2 = {...store.state.contours, ...models2, ...clipOffset(contour, 8)}

    store.update({contours: shapes2});

  };

  reader.readAsText(file);
}

export {uploadSVG};
