import React from 'react';
import {TextInputField} from 'evergreen-ui';
import * as utils from './utils.js'


const testView = (valid = true) => {

  return (
    <div id={"content"}>
    <TextInputField

      onChange={e => {
        let newValid = utils.isNum(e.target.value);

        if (newValid !== valid) {
          let newContent = testView(newValid);
          document.getElementById("content").innerHTML = newContent;
        };

      }}

      isInvalid={!valid}
      description={(valid) ? "" : "please enter a valid number"}
      label="testing"/>
      </div>
  )
}

export {
  testView
}
