import React, { Component, PureComponent } from 'react';
import {Button, Heading, Pane} from 'evergreen-ui';
import './App.css';

import * as utils from './utils.js';
import * as mjs from 'makerjs';
import * as params from './params.js'
import * as s2p from 'shape2path';
import * as svgson from 'svgson';

import {Workplane} from './Workplane.js'
import {MaterialType} from './components/MaterialType.js';
import {MaterialThickness} from './components/MaterialThickness.js';
import {Origin} from './components/Origin.js';
import {Units} from './components/Units.js';
import {PocketButton} from './components/PocketButton.js';
import {AdvancedOptions} from './components/AdvancedOptions.js';
import {ToolpathList} from './components/ToolpathList.js';

import store from "./store.js"; //this is my global state library, this is the only file that will getState() it will pass that to all sub components

import {test, Parent} from './testDec21_2018.js';

class App extends Component {

  state = {
    thicknessSubmitted: false,
    materialSubmitted: false,
    originSubmitted: false,
  }

  uploadSVG(e) {
    //helper function
    const replaceWithPath = (child) => { //why cant I put this in a different file
      console.log("child", child);

      if (child.name === "g") {
        return child.children.map(grandchild => replaceWithPath(grandchild));
      }

      if (child.name === "rectangle") {
        let x;
        let y;
        let width;
        let height;
        let rx;
        let ry;

        let temp = s2p.rect({x, y, width, height, rx, ry});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "circle") {
        let cx;
        let cy;
        let r;

        let temp = s2p.rect({cx, cy, r});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "ellipse") {
        let cx;
        let cy;
        let rx;
        let ry;

        let temp = s2p.ellipse({cx, cy, rx, ry});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "polygon") {
        let points

        let temp = s2p.polygon({points});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "polyline") {
        let points

        let temp = s2p.polyline({points});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "line") {
        let x1;
        let x2;
        let y1;
        let y2;

        let temp = s2p.line({x1, x2, y1, y2});
        return mjs.importer.fromSVGPathData(temp);
      }

      if (child.name === "path") {
        let makerjsPath = mjs.importer.fromSVGPathData(child.attributes.d);
        return makerjsPath;
      }
    }

    //main event
    let files = e.target.files;
    let file = files[0];

    var reader = new FileReader();

    reader.onload = (event) => {
      let text = event.target.result;

      svgson.parse(text).then((json) => {
        let svg = json.children.map(child => {
          return replaceWithPath(child);
        });

        svg = svg.flat(1); //TODO: what does the argument do again?

        //TODO: update state to have makerjs models
        console.log("svg", svg);
        let models = {};

        svg.forEach(shape => {
          models[utils.makeID()] = shape;
        })

        console.log("models", models);

        //ultimately I will set state of app
      });
    };

    reader.readAsText(file);
  }

  render() {
    //should I put everying in a list or all to one model, there seem to be some issues with origins if I mjs.exporter.toSVG() seperately

    let cuts = {};

    store.getState().toolpaths.forEach(path => {
      if (path.selected) {
        cuts[path.id] = path.geometry.drawing;
      }
    })

    let model = {
      models: {
        shapes: {
          models: {
            ...store.getState().shapes
          }
        },
        cuts: {
          models: {
            ...cuts
          }
        }
      }
    }

    model.models.cuts.layer = "red";

    // model = mjs.model.originate(model);
    // model = mjs.model.zero(model);

    model['paths'] = {
      origin: new mjs.paths.Circle(store.getState().compensatedRadius)
    }

    let modelToView = mjs.model.scale(utils.deepcopy(model), 10); //TODO: make drawing easier to see, but this ruins toolpaths

    let viewModel = mjs.exporter.toSVG(modelToView, {
      useSvgPathOnly:false,
      strokeWidth:".25mm",
      // layerOptions:{
      //   cuts: {
      //     stroke: "red"
      //   }
      // },
    });

    viewModel = [viewModel];

    return (
      <div
        className="wrapper"
        onMouseDown={(e) => console.log("div target", e.target.getAttribute("data-route"))}>

        <div className="mainEvent">
          <Workplane groups={viewModel}/>

          <div>
            <input
              id="myInput"
              type="file"
              ref={(ref) => this.upload = ref}
              style={{ display: 'none' }}
              onChange={(e) => this.uploadSVG(e)}/>
            <Button
              marginRight={4}
              onClick={(e) => this.upload.click()}>
                upload (SVG, DXF, BBJS, or Makerjs)
            </Button>
            <Button>
              save
            </Button>
          </div>
        </div>

        <div className="sidebar">
          <div>
            <Heading>Settings</Heading>
            <Pane marginTop={4}> <MaterialType/></Pane>
            <Pane marginTop={4}> <MaterialThickness/></Pane>
            <Pane marginTop={4}> <Units/></Pane>
            <Pane marginTop={4}> <Origin/></Pane>
            <Pane marginTop={4}> <AdvancedOptions default={true} params={store.getState().defaultParameters}/></Pane>
          </div>

          <div>
            <Heading marginTop={8}>Create Toolpaths</Heading>
            <Button marginTop={4}>Profile</Button><br/>
            <Pane marginTop={4}><PocketButton/></Pane>
            <Button marginTop={4} onClick={() => console.log(store.getState())}>Drill</Button><br/>
          </div>

          <div>
            <Heading marginTop={8}>Toolpaths</Heading>
            <ToolpathList marginTop={4} state={store.getState()}/>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
