import React, { Component } from 'react';
import * as utils from './utils.js';


class Workplane extends Component {
  constructor() {
    super();

    this.state = {
      v0: 0,
      v1: 0,
      v2: 500,
      v3: 300,
      mousedown: false,
    }
  }

  wheel(e) {
    e.preventDefault();

    if (e.ctrlKey) {
      e.stopImmediatePropagation();

      if (e.deltaY < 0) {
        console.log('zoom out');
      }

      if (e.deltaY > 0) {
        console.log('zoom in');
      }
        // perform desired zoom action here
    }

    if (e.deltaY < 0) {
      console.log('scrolling up');

      let v0 = this.state.v0 + e.deltaY;
      this.setState({v0});
    }
    if (e.deltaY > 0) {
      console.log('scrolling down');

      let v0 = this.state.v0 + e.deltaY;
      this.setState({v0});
    }
    if (e.deltaX < 0) {
      console.log('scrolling left');

      let v1 = this.state.v1 + e.deltaX;
      this.setState({v1});
    }
    if (e.deltaX > 0) {
      console.log('scrolling right');

      let v1 = this.state.v1 + e.deltaX;
      this.setState({v1});
    }
  }

//   // Find your root SVG element
// var svg = document.querySelector('svg');
//
// // Create an SVGPoint for future math
// var pt = svg.createSVGPoint();
//
// // Get point in global SVG space
// function cursorPoint(evt){
//   pt.x = evt.clientX; pt.y = evt.clientY;
//   return pt.matrixTransform(svg.getScreenCTM().inverse());
// }
//
// svg.addEventListener('mousemove',function(evt){
//   var loc = cursorPoint(evt);
//   // Use loc.x and loc.y here
// },false);

  mouseDown(e) {
    let point = e;

    this.setState({
      mousedown: true
    });
  }

  mouseMove(e) {
    let loc = utils.getSVGpoint(e);

    console.log(loc.x, loc.y)
  }

  mouseUp(e) {
    this.setState({
      mousedown: false
    });
  }

  render() {
    return (
      <svg
        viewBox={`${this.state.v1} ${this.state.v0} ${this.state.v2} ${this.state.v3}`}
        xmlns={"http://www.w3.org/2000/svg"}
        onWheel={(e) => this.wheel(e)}
        onMouseDown={(e) => this.mouseDown(e)}
        onMouseMove={(e) => this.mouseMove(e)}
        onMouseUp={(e) => this.mouseUp(e)}
        style={{border: "1px solid #cccccc"}}>
          <circle cx="50" cy="50" r="50"/>
      </svg>
    );
  }
}

export {Workplane}
