import * as utils from './utils.js';

//sampling rate of gcode points
var minimumSpacing = 2;

//generate gcode data
let cutDepth = 5;
let passDepth = 4;
let toolDiameter = 6;
let numberOfOffsets = 3;
let stepover = 2;
let tolerance = 1;
let climbDirection = false;
let insideCutting = false;
let millOuterFirst = false;
let jogHeight = 1;
let jogRate = 40;
let feedRate = 20;

//derived parameters
let toolRadius = toolDiameter/2;
let nSeries = toolDiameter * stepover;
let compensatedRadius = toolRadius + tolerance;

let tempFloor = Math.ceil(cutDepth/passDepth); //should this be ceiling or floor
let actualPassDepth = cutDepth/tempFloor;

let depthOfPasses = utils.makeSeries(actualPassDepth, actualPassDepth, tempFloor).map(n => -n); //should this be changed right now it occasionally exceeds max stepsize

export {
  compensatedRadius,
  feedRate,
  depthOfPasses,
  jogHeight,
  minimumSpacing,
}
