import React, { Component } from 'react';

const test = (bool, handle) => {
  //console.log(handle);
  return <button onClick={() => handle(!bool)}>hey hey it's {String(bool)}</button>
}

class Parent extends Component {
  //state
  state = {
    valid: true
  }

  //methods
  change = (n) => {
    this.setState({valid: n})
    //this.state.valid = n;
  }

  //render
  render() {
    return <div>
    {test(this.state.valid, this.change)}
    {String(this.state.valid)}
    </div>
  }
}

export {test, Parent}
