import React from 'react';

const isPrimitive = (stroke) => {
  if (stroke.hasOwnProperty("type")) {

    if (stroke.type === "line" ||
        stroke.type === "arc" ||
        stroke.type === "circle" ||
        stroke.type === "bezier") {
          return true;
        } else {
          return false;
        }

  } else {
    return false;
  }
}

export const line = (origin, end) => {
  return {
    type: "line",
    origin,
    end,
  }
}

export const renderLine = (line) => {
  let points = [line.origin, line.end];

  let path = "M " + points.map(([x, y]) => `${x}x ${y}y`);

  let style = {
        fill: "none",
        strokeWidth: "8px",
        stroke: "yellow",
        strokeLinejoin: "round",
        strokeLinecap: "round",
        opacity: ".9"
      }

  return <g><path d={path} style={style}/></g>

}

const arc = (origin, radius, startAngle, endAngle) => {
  return {
    type: "arc",
    origin,
    radius,
    startAngle,
    endAngle,
  }
}

const circle = (origin, radius) => {
  return {
    type: "circle",
    origin,
    radius,
  }
}

const bezier = (origin, cOrigin, end, cEnd) => {
  return {
    type: "bezier",
    origin,
    cOrigin,
    end,
    cEnd
  }
}
