import React, { Component } from 'react';
import {Button, SegmentedControl} from 'evergreen-ui';

class Units extends Component {
  state = {
    options: [
      { label: 'mm', value: 'mm' },
      { label: 'in', value: 'in' },
    ],
    value: 'mm',
  }

  render() {
    return (
      <SegmentedControl
        width={80}
        options={this.state.options}
        value={this.state.value}
        onChange={value => {
          //update app state here
          //this.props.updateparent(value);
          this.props.context.setState({units:value});
          return this.setState({ value });
        }}
      />
    );
  }
}

export {Units};

