import React, { Component } from 'react';
import './App.css';

import * as s2p from 'shape2path';
import * as mjs from 'makerjs';

var fs = require('fs');

//utils
const MakeSeries = (s, n, c) => {
  let l = [];
  l.push(s);

  for (let i = 0; i < c - 1; i++) {
    l.push(l[i] + n);
  }

  return l;
}

const EditSVG  = (svg) => `<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" ${svg.slice(4)}`;

const Download = (filename, text) => {
  var pom = document.createElement('a');

  pom.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
  pom.setAttribute('download', filename);

  if (document.createEvent) {
      var event = document.createEvent('MouseEvents');
      event.initEvent('click', true, true);
      pom.dispatchEvent(event);
  }
  else {
      pom.click();
  }
}

//import svg and turn into paths
let t = s2p.rect({x: 0, y: 120, width: 200, height: 200, rx: 20, ry: 10});
let m = mjs.importer.fromSVGPathData(t);

//generate gcode data
let cutDepth = 1;
let passDepth = .5;
let toolDiameter = .25;
let numberOfOffsets = 3;
let stepover = .125;
let tolerance = .01;
let climbDirection = false;
let insideCutting = false;
let millOuterFirst = false;

let toolRadius = toolDiameter/2;

let nSeries = toolDiameter * stepover;
let compensatedRadius = toolRadius + tolerance;

let tempFloor = Math.floor(cutDepth/passDepth);
let actualPassDepth = cutDepth/tempFloor;

let series0 = MakeSeries(actualPassDepth, actualPassDepth, tempFloor);
series0 = series0.map(n => [0, 0, -n]);

//console.log(series0)

//console.log(MakeSeries(compensatedRadius, nSeries, numberOfOffsets));

let c1 = mjs.model.expandPaths(m, 8, 1);
//console.log(c1);

let c2 = mjs.model.findChains(c1);

let xy = c2[0].links.map(p => p.endPoints).flat(1);
let xy2 = c2[0].links.map(p => p.endPoints[0]);

let scaleFactor = 100;
let scaleXY = xy2.map(p => [p[0]/scaleFactor, p[1]/scaleFactor]);

let gcode = scaleXY.map(p => `G1 X${p[0]} Y${p[1]} F40.0`)
//console.log(c2[0].links);
console.log(gcode);

// write to a new file named
let filename = 'testGcode.nc';
let text = gcode.join('\n');

//Download(filename, text);

let svg = mjs.exporter.toSVG(c1);
let svg2 = mjs.exporter.toSVG(m);

svg = EditSVG(svg);
svg2 = EditSVG(svg2);

console.log(svg2);


class App extends Component {
  render() {
    return (
      <div dangerouslySetInnerHTML={{__html: svg}}/>
    );
  }
}

export default App;
